DROP DATABASE if EXISTS db_books;

CREATE DATABASE db_books;
USE db_books;

CREATE TABLE `livros` 
(
    `id`INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    `titulo`	VARCHAR(100),
    `descricao`	VARCHAR(1200),
    `autor`	VARCHAR(70),
    `categoria`	VARCHAR(70),
    `preco`	DOUBLE,
    `imagem`	VARCHAR(1000)
);

